import React, { useState } from 'react';
import { motion } from 'framer-motion';
import './ReportIssue.css';

export default function ReportIssue() {
  const [phone, setPhone] = useState('');
  const [email, setEmail] = useState('');
  const [title, setTitle] = useState('');
  const [description, setDescription] = useState('');
  const [file, setFile] = useState(null);
  const [notifyByEmail, setNotifyByEmail] = useState(false);

  const handleSubmit = async (e) => {
    e.preventDefault();

    const formData = new FormData();
    formData.append('title', title);
    formData.append('email', email);
    formData.append('description', description);
    formData.append('phone', phone);
    formData.append('notifyByEmail', notifyByEmail);
    if (file) formData.append('file', file);

    try {
      const res = await fetch('http://localhost:5001/api/report', {
        method: 'POST',
        body: formData,
      });

      const data = await res.json();
      alert(data.message);
      setPhone('');
      setEmail('');
      setTitle('');
      setDescription('');
      setFile(null);
      setNotifyByEmail(false);
    } catch (err) {
      console.error('Submit error:', err);
      alert('Failed to submit issue.');
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-emerald-50 to-teal-100 dark:from-gray-900 dark:to-gray-800 flex items-center justify-center p-4 sm:p-6 lg:p-8 overflow-hidden">
      <motion.div
        className="absolute top-10 left-10 w-48 h-48 bg-emerald-200 rounded-full mix-blend-multiply filter blur-xl opacity-50 animate-blob"
        initial={{ scale: 0 }}
        animate={{ scale: 1, rotate: 360 }}
        transition={{ duration: 20, repeat: Infinity, repeatType: 'reverse', ease: 'easeInOut' }}
      ></motion.div>
      <motion.div
        className="absolute bottom-10 right-10 w-48 h-48 bg-teal-200 rounded-full mix-blend-multiply filter blur-xl opacity-50 animate-blob animation-delay-4000"
        initial={{ scale: 0 }}
        animate={{ scale: 1, rotate: -360 }}
        transition={{ duration: 25, repeat: Infinity, repeatType: 'reverse', ease: 'easeInOut' }}
      ></motion.div>
      <motion.div
        className="absolute top-1/2 left-1/2 w-32 h-32 bg-emerald-100 rounded-lg mix-blend-multiply filter blur-lg opacity-60 animate-blob animation-delay-2000"
        initial={{ scale: 0 }}
        animate={{ scale: 1, rotate: 360 }}
        transition={{ duration: 30, repeat: Infinity, repeatType: 'reverse', ease: 'easeInOut' }}
      ></motion.div>

      <motion.div
        initial={{ opacity: 0, y: 50 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.7, ease: 'easeOut' }}
        className="bg-white/70 dark:bg-gray-800 backdrop-blur-xl rounded-xl shadow-2xl p-6 sm:p-8 lg:p-10 w-full max-w-md border border-gray-100 dark:border-gray-700 z-10"
      >
        <h1 className="text-3xl font-extrabold text-center text-emerald-700 dark:text-white mb-6">Report an Issue</h1>
        <p className="text-center text-gray-600 dark:text-gray-200 mb-8">
          We're here to help! Please fill out the form below to report any issues you've encountered.
        </p>

        <form className="space-y-6" onSubmit={handleSubmit}>
          <div>
            <label htmlFor="phone" className="block text-sm font-medium text-gray-700 dark:text-gray-200 mb-1">
              Phone Number
            </label>
            <input
              type="tel"
              id="phone"
              placeholder="e.g., +91 98765 43210"
              value={phone}
              onChange={(e) => setPhone(e.target.value)}
              className="w-full border-gray-300 focus:border-emerald-500 focus:ring-emerald-500 rounded-md shadow-sm p-3 placeholder-gray-400 text-gray-700 dark:text-white dark:bg-gray-900 transition duration-150 ease-in-out"
              required
            />
          </div>

          <div>
            <label htmlFor="email" className="block text-sm font-medium text-gray-700 dark:text-gray-200 mb-1">
              Email Address
            </label>
            <input
              type="email"
              id="email"
              placeholder="you@example.com"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              className="w-full border-gray-300 focus:border-emerald-500 focus:ring-emerald-500 rounded-md shadow-sm p-3 placeholder-gray-400 text-gray-700 dark:text-white dark:bg-gray-900 transition duration-150 ease-in-out"
              required
            />
          </div>

          <div>
            <label htmlFor="title" className="block text-sm font-medium text-gray-700 dark:text-gray-200 mb-1">
              Issue Title
            </label>
            <input
              type="text"
              id="title"
              placeholder="e.g., Login button not working"
              value={title}
              onChange={(e) => setTitle(e.target.value)}
              className="w-full border-gray-300 focus:border-emerald-500 focus:ring-emerald-500 rounded-md shadow-sm p-3 placeholder-gray-400 text-gray-700 dark:text-white dark:bg-gray-900 transition duration-150 ease-in-out"
              required
            />
          </div>

          <div>
            <label htmlFor="description" className="block text-sm font-medium text-gray-700 dark:text-gray-200 mb-1">
              Describe the Issue
            </label>
            <textarea
              id="description"
              placeholder="Please provide as much detail as possible about the issue you're experiencing. When did it happen? What steps did you take?"
              value={description}
              onChange={(e) => setDescription(e.target.value)}
              rows="5"
              className="w-full border-gray-300 focus:border-emerald-500 focus:ring-emerald-500 rounded-md shadow-sm p-3 placeholder-gray-400 text-gray-700 dark:text-white dark:bg-gray-900 resize-y transition duration-150 ease-in-out"
              required
            ></textarea>
          </div>

          <div>
            <label htmlFor="file-upload" className="block text-sm font-medium text-gray-700 dark:text-gray-200 mb-1">
              Attach a Screenshot/File (Optional)
            </label>
            <input
              type="file"
              id="file-upload"
              onChange={(e) => setFile(e.target.files[0])}
              className="block w-full text-sm text-gray-500 dark:text-gray-300 file:mr-4 file:py-2 file:px-4 file:rounded-md file:border-0 file:text-sm file:font-semibold file:bg-emerald-50 file:text-emerald-700 hover:file:bg-emerald-100 cursor-pointer transition duration-150 ease-in-out"
            />
            {file && <p className="mt-2 text-xs text-gray-500 dark:text-gray-300">File selected: {file.name}</p>}
          </div>

          <div className="flex items-center">
            <input
              type="checkbox"
              id="notifyByEmail"
              checked={notifyByEmail}
              onChange={() => setNotifyByEmail(!notifyByEmail)}
              className="h-4 w-4 text-emerald-600 focus:ring-emerald-500 border-gray-300 rounded cursor-pointer accent-emerald-600"
            />
            <label htmlFor="notifyByEmail" className="ml-2 block text-sm text-gray-700 dark:text-gray-200 cursor-pointer">
              Notify me via email when the issue status changes
            </label>
          </div>

          <motion.button
            whileHover={{ scale: 1.05, y: -2 }}
            whileTap={{ scale: 0.95 }}
            transition={{ duration: 0.2, ease: 'easeOut' }}
            type="submit"
            className="w-full bg-emerald-600 text-white font-semibold py-3 px-4 rounded-md shadow-lg hover:bg-emerald-700 focus:outline-none focus:ring-2 focus:ring-emerald-500 focus:ring-offset-2"
          >
            Submit Report
          </motion.button>
        </form>
      </motion.div>
    </div>
  );
}

import React, { useState, useCallback, useMemo } from 'react';
import { motion } from 'framer-motion';
import './ReportIssue.css';

// Debounce hook for form inputs
const useDebounce = (callback, delay) => {
  const [debounceTimer, setDebounceTimer] = useState(null);

  const debouncedCallback = useCallback((...args) => {
    if (debounceTimer) {
      clearTimeout(debounceTimer);
    }
    const newTimer = setTimeout(() => {
      callback(...args);
    }, delay);
    setDebounceTimer(newTimer);
  }, [callback, delay, debounceTimer]);

  return debouncedCallback;
};

// Memoized background animations component
const BackgroundAnimations = React.memo(() => {
  // Simplified animation configs to reduce performance impact
  const animationConfig = {
    duration: 15,
    repeat: Infinity,
    repeatType: 'reverse',
    ease: 'easeInOut'
  };

  return (
    <>
      <motion.div
        className="absolute top-10 left-10 w-32 h-32 bg-emerald-200 rounded-full mix-blend-multiply filter blur-lg opacity-30"
        initial={{ scale: 0.8 }}
        animate={{ scale: 1.2 }}
        transition={animationConfig}
      />
      <motion.div
        className="absolute bottom-10 right-10 w-32 h-32 bg-teal-200 rounded-full mix-blend-multiply filter blur-lg opacity-30"
        initial={{ scale: 0.8 }}
        animate={{ scale: 1.2 }}
        transition={{ ...animationConfig, delay: 2 }}
      />
    </>
  );
});

BackgroundAnimations.displayName = 'BackgroundAnimations';

// Memoized form input component
const FormInput = React.memo(({ 
  type = 'text', 
  id, 
  label, 
  placeholder, 
  value, 
  onChange, 
  required = false,
  rows 
}) => {
  const InputComponent = type === 'textarea' ? 'textarea' : 'input';
  
  return (
    <div>
      <label htmlFor={id} className="block text-sm font-medium text-gray-700 dark:text-gray-200 mb-1">
        {label}
      </label>
      <InputComponent
        type={type !== 'textarea' ? type : undefined}
        id={id}
        placeholder={placeholder}
        value={value}
        onChange={onChange}
        rows={rows}
        className="w-full border-gray-300 focus:border-emerald-500 focus:ring-emerald-500 rounded-md shadow-sm p-3 placeholder-gray-400 text-gray-700 dark:text-white dark:bg-gray-900 transition duration-150 ease-in-out resize-y"
        required={required}
      />
    </div>
  );
});

FormInput.displayName = 'FormInput';

export default React.memo(function ReportIssue() {
  const [formData, setFormData] = useState({
    phone: '',
    email: '',
    title: '',
    description: '',
    notifyByEmail: false
  });
  const [file, setFile] = useState(null);
  const [isSubmitting, setIsSubmitting] = useState(false);

  // Debounced form update function
  const debouncedUpdate = useDebounce((field, value) => {
    setFormData(prev => ({ ...prev, [field]: value }));
  }, 150);

  // Memoized form handlers
  const handleInputChange = useCallback((field) => (e) => {
    const value = e.target.type === 'checkbox' ? e.target.checked : e.target.value;
    debouncedUpdate(field, value);
  }, [debouncedUpdate]);

  const handleFileChange = useCallback((e) => {
    setFile(e.target.files[0]);
  }, []);

  const handleSubmit = useCallback(async (e) => {
    e.preventDefault();
    
    if (isSubmitting) return;
    setIsSubmitting(true);

    const formDataToSend = new FormData();
    Object.entries(formData).forEach(([key, value]) => {
      formDataToSend.append(key, value);
    });
    if (file) formDataToSend.append('file', file);

    try {
      const res = await fetch('http://localhost:5001/api/report', {
        method: 'POST',
        body: formDataToSend,
      });

      const data = await res.json();
      alert(data.message);
      
      // Reset form
      setFormData({
        phone: '',
        email: '',
        title: '',
        description: '',
        notifyByEmail: false
      });
      setFile(null);
    } catch (err) {
      console.error('Submit error:', err);
      alert('Failed to submit issue.');
    } finally {
      setIsSubmitting(false);
    }
  }, [formData, file, isSubmitting]);

  // Memoized form fields configuration
  const formFields = useMemo(() => [
    {
      id: 'phone',
      type: 'tel',
      label: 'Phone Number',
      placeholder: 'e.g., +91 98765 43210',
      required: true
    },
    {
      id: 'email',
      type: 'email',
      label: 'Email Address',
      placeholder: 'you@example.com',
      required: true
    },
    {
      id: 'title',
      type: 'text',
      label: 'Issue Title',
      placeholder: 'e.g., Login button not working',
      required: true
    },
    {
      id: 'description',
      type: 'textarea',
      label: 'Describe the Issue',
      placeholder: 'Please provide as much detail as possible about the issue you\'re experiencing. When did it happen? What steps did you take?',
      rows: 5,
      required: true
    }
  ], []);

  return (
    <div className="min-h-screen bg-gradient-to-br from-emerald-50 to-teal-100 dark:from-gray-900 dark:to-gray-800 flex items-center justify-center p-4 sm:p-6 lg:p-8 overflow-hidden">
      <BackgroundAnimations />

      <motion.div
        initial={{ opacity: 0, y: 30 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.5, ease: 'easeOut' }}
        className="bg-white/70 dark:bg-gray-800 backdrop-blur-lg rounded-xl shadow-2xl p-6 sm:p-8 lg:p-10 w-full max-w-md border border-gray-100 dark:border-gray-700 z-10"
      >
        <h1 className="text-3xl font-extrabold text-center text-emerald-700 dark:text-white mb-6">Report an Issue</h1>
        <p className="text-center text-gray-600 dark:text-gray-200 mb-8">
          We're here to help! Please fill out the form below to report any issues you've encountered.
        </p>

        <form className="space-y-6" onSubmit={handleSubmit}>
          {formFields.map((field) => (
            <FormInput
              key={field.id}
              {...field}
              value={formData[field.id]}
              onChange={handleInputChange(field.id)}
            />
          ))}

          <div>
            <label htmlFor="file-upload" className="block text-sm font-medium text-gray-700 dark:text-gray-200 mb-1">
              Attach a Screenshot/File (Optional)
            </label>
            <input
              type="file"
              id="file-upload"
              onChange={handleFileChange}
              className="block w-full text-sm text-gray-500 dark:text-gray-300 file:mr-4 file:py-2 file:px-4 file:rounded-md file:border-0 file:text-sm file:font-semibold file:bg-emerald-50 file:text-emerald-700 hover:file:bg-emerald-100 cursor-pointer transition duration-150 ease-in-out"
            />
            {file && <p className="mt-2 text-xs text-gray-500 dark:text-gray-300">File selected: {file.name}</p>}
          </div>

          <div className="flex items-center">
            <input
              type="checkbox"
              id="notifyByEmail"
              checked={formData.notifyByEmail}
              onChange={handleInputChange('notifyByEmail')}
              className="h-4 w-4 text-emerald-600 focus:ring-emerald-500 border-gray-300 rounded cursor-pointer accent-emerald-600"
            />
            <label htmlFor="notifyByEmail" className="ml-2 block text-sm text-gray-700 dark:text-gray-200 cursor-pointer">
              Notify me via email when the issue status changes
            </label>
          </div>

          <motion.button
            whileHover={{ scale: 1.02 }}
            whileTap={{ scale: 0.98 }}
            transition={{ duration: 0.1 }}
            type="submit"
            disabled={isSubmitting}
            className="w-full bg-emerald-600 text-white font-semibold py-3 px-4 rounded-md shadow-lg hover:bg-emerald-700 focus:outline-none focus:ring-2 focus:ring-emerald-500 focus:ring-offset-2 disabled:opacity-50 disabled:cursor-not-allowed transition-colors duration-200"
          >
            {isSubmitting ? 'Submitting...' : 'Submit Report'}
          </motion.button>
        </form>
      </motion.div>
    </div>
  );
});

import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { toast ,ToastContainer} from 'react-toastify';
const AdminDashboard = () => {
  const navigate = useNavigate();
  const [issues, setIssues] = useState([]);

  // Fetch all reported issues
  const fetchIssues = React.useCallback(async () => {
    try {
      const token = localStorage.getItem('token');
      const res = await fetch('http://localhost:5001/api/issues', {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });

      if (!res.ok) {
        throw new Error('Unauthorized or failed to fetch');
      }

      const data = await res.json();
      setIssues(data);
    } catch (error) {
      console.error('Error fetching issues:', error);
      alert('Failed to fetch issues. Please login again.');
      navigate('/login');
    }
  }, [navigate]);

  // Update issue status
  const handleStatusChange = async (id, status) => {
    try {
      const token = localStorage.getItem('token');
      const res = await fetch(`http://localhost:5001/api/issues/${id}/status`, {
        method: 'PATCH',
        headers: {
          'Content-Type': 'application/json',
          Authorization: `Bearer ${token}`,
        },
        body: JSON.stringify({ status }),
      });

      if (!res.ok) {
        throw new Error('Failed to update status');
      }

      // Refresh issue list
      fetchIssues();
    } catch (error) {
      console.error('Error updating status:', error);
      alert('Could not update status.');
    }
  };

  useEffect(() => {
    fetchIssues();
  }, [fetchIssues]);

  return (
    <div className="min-h-screen bg-gray-50">
      {/* Issues Table */}
      <main className="p-6">
        <h2 className="text-xl font-bold mb-4">Admin Dashboard</h2>
        <p>Welcome, Admin! You have access to admin controls.</p>
        <ToastContainer
        position="top-right"
        autoClose={3000}
        closeOnClick
        pauseOnHover
        draggable
        theme="dark"
        toastClassName="toast-body custom-toast-shadow"
        bodyClassName="text-sm font-medium"
      />
        <h3 className="text-lg font-semibold mb-4">Reported Issues</h3>

        {issues.length === 0 ? (
          <p>No issues found.</p>
        ) : (
          <div className="overflow-x-auto">
            <table className="min-w-full border border-gray-300 bg-white text-sm rounded-lg overflow-hidden shadow">
              <thead className="bg-gray-200 text-gray-700">
                <tr>
                  <th className="p-3">Title</th>
                  <th className="p-3">Description</th>
                  <th className="p-3">Phone</th>
                  <th className="p-3">Email</th>
                  <th className="p-3">Status</th>
                  <th className="p-3">Change Status</th>
                </tr>
              </thead>
              <tbody>
                {issues.map((issue) => (
                  <tr key={issue._id} className="border-t border-gray-200">
                    <td className="p-3">{issue.title}</td>
                    <td className="p-3">{issue.description}</td>
                    <td className="p-3">{issue.phone}</td>
                    <td className="p-3">{issue.email}</td>
                    <td className="p-3">{issue.status || 'Pending'}</td>
                    <td className="p-3">
                      <select
                        value={issue.status || 'Pending'}
                        onChange={(e) => handleStatusChange(issue._id, e.target.value)}
                        className="border border-gray-300 rounded px-2 py-1"
                      >
                        <option>Pending</option>
                        <option>In Progress</option>
                        <option>Resolved</option>
                        <option>Rejected</option>
                      </select>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        )}
      </main>
    </div>
  );
};

export default AdminDashboard;

import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { motion } from 'framer-motion';
import { toast, ToastContainer } from 'react-toastify';

const AdminDashboard = () => {
  const navigate = useNavigate();
  const [issues, setIssues] = useState([]);

  // Animation variants for the page container
  const pageVariants = {
    initial: { opacity: 0, y: 20 },
    animate: { 
      opacity: 1, 
      y: 0,
      transition: { 
        duration: 0.6, 
        ease: "easeOut",
        staggerChildren: 0.1
      }
    },
    exit: { 
      opacity: 0, 
      y: -20,
      transition: { duration: 0.4 }
    }
  };

  // Animation variants for table rows
  const rowVariants = {
    initial: { opacity: 0, x: -20 },
    animate: { 
      opacity: 1, 
      x: 0,
      transition: { duration: 0.4 }
    }
  };

  // Fetch all reported issues
  const fetchIssues = React.useCallback(async () => {
    try {
      const token = localStorage.getItem('token');
      const res = await fetch('http://localhost:5001/api/issues', {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });

      if (!res.ok) {
        throw new Error('Unauthorized or failed to fetch');
      }

      const data = await res.json();
      setIssues(data);
    } catch (error) {
      console.error('Error fetching issues:', error);
      alert('Failed to fetch issues. Please login again.');
      navigate('/login');
    }
  }, [navigate]);

  // Update issue status
  const handleStatusChange = async (id, status) => {
    try {
      const token = localStorage.getItem('token');
      const res = await fetch(`http://localhost:5001/api/issues/${id}/status`, {
        method: 'PATCH',
        headers: {
          'Content-Type': 'application/json',
          Authorization: `Bearer ${token}`,
        },
        body: JSON.stringify({ status }),
      });

      if (!res.ok) {
        throw new Error('Failed to update status');
      }

      // Refresh issue list
      fetchIssues();
    } catch (error) {
      console.error('Error updating status:', error);
      alert('Could not update status.');
    }
  };

  useEffect(() => {
    fetchIssues();
  }, [fetchIssues]);

  return (
    <motion.div 
      className="min-h-screen bg-gray-50"
      variants={pageVariants}
      initial="initial"
      animate="animate"
      exit="exit"
    >
      {/* Issues Table */}
      <main className="p-6">
        <motion.h2 
          className="text-xl font-bold mb-4"
          initial={{ opacity: 0, y: -20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.6, delay: 0.1 }}
        >
          Admin Dashboard
        </motion.h2>
        <motion.p
          initial={{ opacity: 0, y: -10 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.6, delay: 0.2 }}
        >
          Welcome, Admin! You have access to admin controls.
        </motion.p>
        <ToastContainer
          position="top-right"
          autoClose={3000}
          closeOnClick
          pauseOnHover
          draggable
          theme="dark"
          toastClassName="toast-body custom-toast-shadow"
          bodyClassName="text-sm font-medium"
        />
        <motion.h3 
          className="text-lg font-semibold mb-4"
          initial={{ opacity: 0, y: -10 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.6, delay: 0.3 }}
        >
          Reported Issues
        </motion.h3>

        {issues.length === 0 ? (
          <motion.p
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            transition={{ duration: 0.6, delay: 0.4 }}
          >
            No issues found.
          </motion.p>
        ) : (
          <motion.div 
            className="overflow-x-auto"
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.6, delay: 0.4 }}
          >
            <table className="min-w-full border border-gray-300 bg-white text-sm rounded-lg overflow-hidden shadow">
              <thead className="bg-gray-200 text-gray-700">
                <motion.tr
                  initial={{ opacity: 0 }}
                  animate={{ opacity: 1 }}
                  transition={{ duration: 0.5, delay: 0.5 }}
                >
                  <th className="p-3">Title</th>
                  <th className="p-3">Description</th>
                  <th className="p-3">Phone</th>
                  <th className="p-3">Email</th>
                  <th className="p-3">Status</th>
                  <th className="p-3">Change Status</th>
                </motion.tr>
              </thead>
              <tbody>
                {issues.map((issue, index) => (
                  <motion.tr 
                    key={issue._id} 
                    className="border-t border-gray-200"
                    variants={rowVariants}
                    initial="initial"
                    animate="animate"
                    transition={{ delay: 0.6 + (index * 0.1) }}
                  >
                    <td className="p-3">{issue.title}</td>
                    <td className="p-3">{issue.description}</td>
                    <td className="p-3">{issue.phone}</td>
                    <td className="p-3">{issue.email}</td>
                    <td className="p-3">{issue.status || 'Pending'}</td>
                    <td className="p-3">
                      <select
                        value={issue.status || 'Pending'}
                        onChange={(e) => handleStatusChange(issue._id, e.target.value)}
                        className="border border-gray-300 rounded px-2 py-1"
                      >
                        <option>Pending</option>
                        <option>In Progress</option>
                        <option>Resolved</option>
                        <option>Rejected</option>
                      </select>
                    </td>
                  </motion.tr>
                ))}
              </tbody>
            </table>
          </motion.div>
        )}
      </main>
    </motion.div>
  );
};

export default AdminDashboard;

